sys_script_client.script:
function onLoad() {

	try{
		var votoIniziale=0;    
		jslog('Il valore assegnato a votoIniziale è '+votoIniziale);

		//votoIniziale=valore; // scorretto perché valore non è stato definito
		jslog('Ok funziona');

   if(g_form.isNewRecord()){      // solo quando sto creando un nuovo record
   g_form.setValue('voto_numerico',votoIniziale);   // set valore iniziale uguale a votoIniziale

  }}catch(err){
   jslog('Errore durante esecuzione di script javascript: ' + err);
  }


}
