sysauto_script.script:
// Devo leggere valore soglia voto esame e assegnarlo a una variabile
var sogliaEsame=gs.getProperty('x_570200_esami_2.numSogliaVotoSufficiente');
// memorizziamo in una var chiamata esami2records l'intera tabella Esami2
var esami2records=new GlideRecord('x_570200_esami_2_esami2');

//filtro solo quelli in valutazione e iscritti
esami2records.addQuery('stato','<',3);
esami2records.query();
// scorro tutti i record per applicare condizione
while(esami2records.next()){
	if(esami2records.voto_numerico>=sogliaEsame){
		esami2records.stato=4; // set to sostenuto
	}else{esami2records.stato=3;} // set to rimandato
	esami2records.update();
}
